<boltArtifact id="mobile-testing-platform-enhanced" title="Enhanced Mobile Device Testing Platform">
<boltAction type="file" filePath="app/types/device.ts">
export interface ScreenSize {
  width: number;
  height: number;
  ppi?: number; // pixels per inch
  aspectRatio?: string;
}

export interface DeviceOS {
  name: string;
  version: string;
  releaseDate: Date;
}

export interface Device {
  id: string;
  name: string;
  type: 'phone' | 'tablet' | 'laptop' | 'desktop';
  manufacturer: string;
  screenSizes: ScreenSize[];
  supportedOS: DeviceOS[];
  isActive: boolean;
  lastUpdated: Date;
  userAgent?: string;
}

export interface DeviceTestResult {
  device: Device;
  screenSize: ScreenSize;
  url: string;
  testDate: Date;
  status: 'pending' | 'completed' | 'error';
  compatibility: 'fully_compatible' | 'partially_compatible' | 'incompatible';
  performanceScore?: number;
  renderIssues?: string[];
  responsiveDesignScore?: number;
}
</boltAction>

<boltAction type="file" filePath="app/services/device-service.ts">
import { Device, DeviceOS } from '~/types/device';

export class DeviceService {
  private static DEVICE_CATALOG_URL = '/api/devices/catalog';
  private static DEVICE_UPDATE_INTERVAL = 24 * 60 * 60 * 1000; // 24 hours

  static async fetchDevices(): Promise<Device[]> {
    try {
      const response = await fetch(this.DEVICE_CATALOG_URL);
      if (!response.ok) {
        throw new Error('Failed to fetch devices');
      }
      return await response.json();
    } catch (error) {
      console.error('Device fetch error:', error);
      return this.getDefaultDevices();
    }
  }

  static getDefaultDevices(): Device[] {
    return [
      {
        id: 'iphone-15-pro',
        name: 'iPhone 15 Pro',
        type: 'phone',
        manufacturer: 'Apple',
        screenSizes: [
          { 
            width: 1179, 
            height: 2556, 
            ppi: 460,
            aspectRatio: '19.5:9'
          }
        ],
        supportedOS: [
          { 
            name: 'iOS', 
            version: '17', 
            releaseDate: new Date('2023-09-20') 
          }
        ],
        isActive: true,
        lastUpdated: new Date(),
        userAgent: 'Mozilla/5.0 (iPhone; CPU iPhone OS 17_0 like Mac OS X)'
      },
      {
        id: 'samsung-s23',
        name: 'Samsung Galaxy S23',
        type: 'phone',
        manufacturer: 'Samsung',
        screenSizes: [
          { 
            width: 1080, 
            height: 2340, 
            ppi: 425,
            aspectRatio: '20:9'
          }
        ],
        supportedOS: [
          { 
            name: 'Android', 
            version: '14', 
            releaseDate: new Date('2023-10-04') 
          }
        ],
        isActive: true,
        lastUpdated: new Date(),
        userAgent: 'Mozilla/5.0 (Linux; Android 14; SM-S911B)'
      }
    ];
  }

  static async syncDeviceCatalog(): Promise<Device[]> {
    try {
      const devices = await this.fetchDevices();
      // Additional logic for syncing and updating devices
      return devices;
    } catch (error) {
      console.error('Device sync error:', error);
      return this.getDefaultDevices();
    }
  }
}
</boltAction>

<boltAction type="file" filePath="app/services/testing-service.ts">
import { Device, DeviceTestResult, ScreenSize } from '~/types/device';

export class TestingService {
  static async testWebsiteCompatibility(
    url: string, 
    device: Device, 
    screenSize: ScreenSize
  ): Promise<DeviceTestResult> {
    try {
      // Simulated testing logic
      const response = await fetch('/api/website/test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          url,
          device,
          screenSize
        })
      });

      if (!response.ok) {
        throw new Error('Website testing failed');
      }

      const testResult: DeviceTestResult = await response.json();
      return {
        ...testResult,
        testDate: new Date(),
        status: 'completed'
      };
    } catch (error) {
      console.error('Website testing error:', error);

      // Fallback mock result
      return {
        device,
        screenSize,
        url,
        testDate: new Date(),
        status: 'error',
        compatibility: 'incompatible',
        performanceScore: 0,
        renderIssues: ['Failed to load website']
      };
    }
  }

  static calculateCompatibilityScore(result: DeviceTestResult): number {
    if (result.status === 'error') return 0;

    let score = 0;
    if (result.compatibility === 'fully_compatible') score += 100;
    if (result.compatibility === 'partially_compatible') score += 50;

    score += (result.performanceScore || 0);
    score += (result.responsiveDesignScore || 0);

    return Math.min(score, 100);
  }
}
</boltAction>

<boltAction type="file" filePath="app/routes/_index.tsx">
import { useState, useEffect } from 'react';
import { DeviceService } from '~/services/device-service';
import { TestingService } from '~/services/testing-service';
import { Device, ScreenSize, DeviceTestResult } from '~/types/device';

export default function Index() {
  const [devices, setDevices] = useState<Device[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<Device | null>(null);
  const [selectedScreenSize, setSelectedScreenSize] = useState<ScreenSize | null>(null);
  const [url, setUrl] = useState('');
  const [testResults, setTestResults] = useState<DeviceTestResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    async function loadDevices() {
      const fetchedDevices = await DeviceService.syncDeviceCatalog();
      setDevices(fetchedDevices);
    }
    loadDevices();
  }, []);

  const handleTestWebsite = async () => {
    if (!url || !selectedDevice || !selectedScreenSize) {
      alert('Please complete all selections');
      return;
    }

    setIsLoading(true);
    try {
      const result = await TestingService.testWebsiteCompatibility(
        url, 
        selectedDevice, 
        selectedScreenSize
      );

      setTestResults(prev => [result, ...prev]);
    } catch (error) {
      console.error('Testing failed', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Mobile Device Testing Platform</h1>

      <div className="grid md:grid-cols-2 gap-6">
        {/* Device Selection */}
        <div className="bg-white shadow-md rounded-lg p-4">
          <h2 className="text-xl font-semibold mb-4">Select Device</h2>
          <select 
            className="w-full p-2 border rounded"
            onChange={(e) => {
              const device = devices.find(d => d.id === e.target.value);
              setSelectedDevice(device || null);
              setSelectedScreenSize(device?.screenSizes[0] || null);
            }}
          >
            <option value="">Select a Device</option>
            {devices.map(device => (
              <option key={device.id} value={device.id}>
                {device.name} - {device.manufacturer}
              </option>
            ))}
          </select>

          {selectedDevice && (
            <div className="mt-4">
              <h3 className="font-medium">Screen Sizes</h3>
              <select 
                className="w-full p-2 border rounded"
                onChange={(e) => {
                  const size = selectedDevice.screenSizes.find(
                    s => `${s.width}x${s.height}` === e.target.value
                  );
                  setSelectedScreenSize(size || null);
                }}
              >
                {selectedDevice.screenSizes.map(size => (
                  <option key={`${size.width}x${size.height}`} value={`${size.width}x${size.height}`}>
                    {size.width} x {size.height}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>

        {/* Website Testing */}
        <div className="bg-white shadow-md rounded-lg p-4">
          <h2 className="text-xl font-semibold mb-4">Test Website</h2>
          <input 
            type="url"
            placeholder="Enter website URL"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            className="w-full p-2 border rounded mb-4"
          />
          <button 
            onClick={handleTestWebsite}
            disabled={isLoading}
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 disabled:opacity-50"
          >
            {isLoading ? 'Testing...' : 'Test Compatibility'}
          </button>
        </div>
      </div>

      {/* Test Results */}
      <div className="mt-6">
        <h2 className="text-2xl font-bold mb-4">Test Results</h2>
        {testResults.map((result, index) => (
          <div 
            key={index} 
            className={`
              p-4 mb-4 rounded-lg 
              ${result.compatibility === 'fully_compatible' ? 'bg-green-100' : 
                result.compatibility === 'partially_compatible' ? 'bg-yellow-100' : 'bg-red-100'}
            `}
          >
            <div className="flex justify-between">
              <h3 className="font-semibold">{result.device.name}</h3>
              <span>{result.testDate.toLocaleString()}</span>
            </div>
            <p>URL: {result.url}</p>
            <p>Compatibility: {result.compatibility}</p>
            {result.renderIssues && result.renderIssues.length > 0 && (
              <div>
