import { Device, DeviceTestResult, ScreenSize } from '~/types/device';

export class TestingService {
  static async testWebsiteCompatibility(
    url: string, 
    device: Device, 
    screenSize: ScreenSize
  ): Promise<DeviceTestResult> {
    try {
      // Simulated testing logic
      const response = await fetch('/api/website/test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          url,
          device,
          screenSize
        })
      });

      if (!response.ok) {
        throw new Error('Website testing failed');
      }

      const testResult: DeviceTestResult = await response.json();
      return {
        ...testResult,
        testDate: new Date(),
        status: 'completed'
      };
    } catch (error) {
      console.error('Website testing error:', error);

      // Fallback mock result
      return {
        device,
        screenSize,
        url,
        testDate: new Date(),
        status: 'error',
        compatibility: 'incompatible',
        performanceScore: 0,
        renderIssues: ['Failed to load website']
      };
    }
  }

  static calculateCompatibilityScore(result: DeviceTestResult): number {
    if (result.status === 'error') return 0;

    let score = 0;
    if (result.compatibility === 'fully_compatible') score += 100;
    if (result.compatibility === 'partially_compatible') score += 50;

    score += (result.performanceScore || 0);
    score += (result.responsiveDesignScore || 0);

    return Math.min(score, 100);
  }
}
</boltAction>

