      fullyCompatiblePercentage: (compatibilityCounts.fully_compatible / totalTests) * 100,
      partiallyCompatiblePercentage: (compatibilityCounts.partially_compatible / totalTests) * 100,
      incompatiblePercentage: (compatibilityCounts.incompatible / totalTests) * 100
    };
  }
}
