import { Device, DeviceTestResult, ScreenSize } from '~/types/device';
import { ApiClient } from '~/utils/api-client';
import { AnalyticsService } from './analytics-service';

export class EnhancedTestingService {
  private static TESTING_ENDPOINT = '/website/test';

  static async testWebsiteCompatibility(
    url: string, 
    device: Device, 
    screenSize: ScreenSize
  ): Promise<DeviceTestResult> {
    try {
      // Simulate comprehensive website testing
      const testResult = await ApiClient.post(this.TESTING_ENDPOINT, {
        url,
        device,
        screenSize,
        testConfig: {
          checkResponsiveness: true,
          checkPerformance: true,
          checkAccessibility: true
        }
      });

      // Track the test result for analytics
      await AnalyticsService.trackTestResult(testResult);

      return {
        ...testResult,
        testDate: new Date(),
        status: 'completed'
      };
    } catch (error) {
      console.error('Advanced website testing error:', error);

      const fallbackResult: DeviceTestResult = {
        device,
        screenSize,
        url,
        testDate: new Date(),
        status: 'error',
        compatibility: 'incompatible',
        performanceScore: 0,
        responsiveDesignScore: 0,
        renderIssues: [
          'Failed to load website',
          'Network error or timeout',
          'Potential CORS or security restrictions'
        ]
      };

      return fallbackResult;
    }
  }

  static generateDetailedReport(results: DeviceTestResult[]) {
    const globalTrend = AnalyticsService.calculateGlobalCompatibilityTrend(results);

    return {
      totalTests: results.length,
      compatibilityTrend: globalTrend,
      topIssues: this.aggregateCommonIssues(results),
      bestPerformingDevices: this.identifyBestPerformingDevices(results)
    };
  }

  private static aggregateCommonIssues(results: DeviceTestResult[]) {
    const issueFrequency: Record<string, number> = {};

    results.forEach(result => {
      result.renderIssues?.forEach(issue => {
        issueFrequency[issue] = (issueFrequency[issue] || 0) + 1;
      });
    });

    return Object.entries(issueFrequency)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 5)
      .map(([issue, count]) => ({ issue, count }));
  }

  private static identifyBestPerformingDevices(results: DeviceTestResult[]) {
    const devicePerformance: Record<string, number[]> = {};

    results.forEach(result => {
      const deviceKey = `${result.device.name}-${result.device.manufacturer}`;
      const performanceScore = result.performanceScore || 0;

      if (!devicePerformance[deviceKey]) {
        devicePerformance[deviceKey] = [];
      }
      devicePerformance[deviceKey].push(performanceScore);
    });

    return Object.entries(devicePerformance)
      .map(([deviceKey, scores]) => ({
        deviceKey,
        averagePerformance: scores.reduce((a, b) => a + b, 0) / scores.length
      }))
      .sort((a, b) => b.averagePerformance - a.averagePerformance)
      .slice(0, 3);
  }
}
</boltAction>

