        screenWidth: config.device.screenWidth,
        screenHeight: config.device.screenHeight,
        osVersion: config.device.osVersion,
        previousCompatibilityScore: performanceScore
      });

      return {
        url: config.url,
        performanceScore,
        responsiveScore,
        accessibilityIssues,
        compatibilityPrediction,
        renderIssues: accessibilityIssues.map(issue => issue.message)
      };
    } catch (error) {
      console.error('Website testing error:', error);
      return {
        url: config.url,
        performanceScore: 0,
        responsiveScore: 0,
        accessibilityIssues: [],
        compatibilityPrediction: 0,
        renderIssues: ['Failed to load website']
      };
    } finally {
      await browser.close();
    }
  }

  private static calculatePerformanceScore(metrics: Record<string, number>): number {
    // Custom performance scoring logic
    const scoreFactors = [
      metrics.JSHeapUsedSize / metrics.JSHeapTotalSize,
      1 - (metrics.TaskDuration / 1000),
      1 - (metrics.LayoutCount / 100)
    ];

    const averageScore = scoreFactors.reduce((a, b) => a + b, 0) / scoreFactors.length;
    return Math.max(0, Math.min(100, averageScore * 100));
  }

  private static async checkResponsiveDesign(page: puppeteer.Page): Promise<number> {
    const viewports = [
      { width: 320, height: 568 },   // Mobile
      { width: 768, height: 1024 },  // Tablet
      { width: 1920, height: 1080 }  // Desktop
    ];

    let responsiveScore = 0;
    for (const viewport of viewports) {
      await page.setViewport(viewport);

      // Check if layout adapts
      const elementCount = await page.evaluate(() => {
        return document.querySelectorAll('*').length;
      });

      // Simple responsive check
      responsiveScore += elementCount > 0 ? 33.33 : 0;
    }

    return responsiveScore;
  }

  private static async checkAccessibility(page: puppeteer.Page) {
    const issues = await page.evaluate(() => {
      const accessibilityIssues: { message: string }[] = [];

      // Check for alt text on images
      document.querySelectorAll('img:not([alt])').forEach(() => {
        accessibilityIssues.push({ 
          message: 'Missing alt text on image' 
        });
      });

      // Check color contrast
      const elements = document.querySelectorAll('*');
      elements.forEach(el => {
        const style = window.getComputedStyle(el);
        const color = style.color;
        const backgroundColor = style.backgroundColor;

        // Basic contrast check (you'd use a more sophisticated algorithm in production)
        if (!isGoodContrast(color, backgroundColor)) {
          accessibilityIssues.push({
            message: 'Poor color contrast'
          });
        }
      });

      // Helper function for contrast check
      function isGoodContrast(color1: string, color2: string): boolean {
        // Simplified contrast calculation
        return Math.abs(luminance(color1) - luminance(color2)) > 0.5;
      }

      function luminance(color: string): number {
        // Convert color to RGB and calculate luminance
        const rgb = color.match(/\d+/g)?.map(Number) || [0, 0, 0];
        const [r, g, b] = rgb.map(c => {
          c /= 255;
          return c <= 0.03928 
            ? c / 12.92 
            : Math.pow((c + 0.055) / 1.055, 2.4);
        });
        return 0.2126 * r + 0.7152 * g + 0.0722 * b;
      }

      return accessibilityIssues;
    });

    return issues;
  }
}
</boltAction>

