import { Performance } from '~/types/performance';

export class PerformanceTracker {
  private static instance: PerformanceTracker;
  private performanceLog: Performance[] = [];

  private constructor() {}

  public static getInstance(): PerformanceTracker {
    if (!PerformanceTracker.instance) {
      PerformanceTracker.instance = new PerformanceTracker();
    }
    return PerformanceTracker.instance;
  }

  recordPerformance(performance: Performance) {
    this.performanceLog.push({
      ...performance,
      timestamp: new Date()
    });

    // Limit log size
    if (this.performanceLog.length > 1000) {
      this.performanceLog.shift();
    }

    // Async send to backend
    this.sendPerformanceData(performance);
  }

  private async sendPerformanceData(performance: Performance) {
    try {
      await fetch('/api/performance/track', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(performance)
      });
    } catch (error) {
      console.error('Performance tracking failed', error);
    }
  }

  getRecentPerformance(limit = 50): Performance[] {
    return this.performanceLog.slice(-limit);
  }

  analyzePerformanceTrends() {
    const performances = this.performanceLog;

    return {
      averageResponseTime: this.calculateAverageResponseTime(performances),
      successRate: this.calculateSuccessRate(performances),
      topPerformingDevices: this.identifyTopPerformingDevices(performances)
    };
  }

  private calculateAverageResponseTime(performances: Performance[]): number {
    if (performances.length === 0) return 0;

    const totalResponseTime = performances.reduce(
      (sum, perf) => sum + (perf.responseTime || 0), 
      0
    );

    return totalResponseTime / performances.length;
  }

  private calculateSuccessRate(performances: Performance[]): number {
    if (performances.length === 0) return 0;

    const successfulTests = performances.filter(
      perf => perf.status === 'success'
    ).length;

    return (successfulTests / performances.length) * 100;
  }

  private identifyTopPerformingDevices(performances: Performance[]) {
    const devicePerformance: Record<string, number[]> = {};

    performances.forEach(perf => {
      if (perf.device) {
        const key = `${perf.device.name}-${perf.device.type}`;
        if (!devicePerformance[key]) {
          devicePerformance[key] = [];
        }
        devicePerformance[key].push(perf.responseTime || 0);
      }
    });

    return Object.entries(devicePerformance)
      .map(([deviceKey, responseTimes]) => ({
        deviceKey,
        averageResponseTime: 
          responseTimes.reduce((a, b) => a + b, 0) / responseTimes.length
      }))
      .sort((a, b) => a.averageResponseTime - b.averageResponseTime)
      .slice(0, 5);
  }
}

// Singleton instance
export const performanceTracker = PerformanceTracker.getInstance();
</boltAction>

