import { Device } from '~/types/device';

export class DeviceSynchronizationService {
  private static DEVICE_SYNC_INTERVAL = 24 * 60 * 60 * 1000; // 24 hours
  private static DEVICE_SOURCES = [
    'https://deviceatlas.com/api/devices',
    'https://wurfl.io/device-data',
    'https://devicespecifications.com/api'
  ];

  static async synchronizeDevices(): Promise<Device[]> {
    const devicePromises = this.DEVICE_SOURCES.map(async (source) => {
      try {
        const response = await fetch(source, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${process.env.DEVICE_API_TOKEN}`,
            'Accept': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch from ${source}`);
        }

        return await response.json();
      } catch (error) {
        console.error(`Device sync error from ${source}:`, error);
        return [];
      }
    });

    const deviceResults = await Promise.allSettled(devicePromises);

    const newDevices: Device[] = deviceResults
      .filter(result => result.status === 'fulfilled')
      .flatMap(result => 
        result.status === 'fulfilled' ? result.value : []
      );

    // Validate and transform devices
    const processedDevices = this.processDevices(newDevices);

    // Save to database
    await this.saveDevicesToDatabase(processedDevices);

    return processedDevices;
  }

  private static processDevices(devices: any[]): Device[] {
    return devices.map(device => ({
      id: device.id || crypto.randomUUID(),
      name: device.name,
      type: this.normalizeDeviceType(device.type),
      manufacturer: device.manufacturer,
      screenSizes: device.screenSizes || [],
      supportedOS: device.supportedOS || [],
      isActive: true,
      lastUpdated: new Date(),
      userAgent: device.userAgent
    })).filter(this.validateDevice);
  }

  private static normalizeDeviceType(type: string): Device['type'] {
    const typeMap: Record<string, Device['type']> = {
      'mobile': 'phone',
      'smartphone': 'phone',
      'tablet': 'tablet',
      'laptop': 'laptop',
      'desktop': 'desktop'
    };
    return typeMap[type.toLowerCase()] || 'phone';
  }

  private static validateDevice(device: Device): boolean {
    return !!(
      device.name && 
      device.manufacturer && 
      device.screenSizes.length > 0
    );
  }

  private static async saveDevicesToDatabase(devices: Device[]) {
    // Implement database saving logic
    // This would typically use Prisma or another ORM
    try {
      // Example with Prisma
      // await prisma.device.createMany({
      //   data: devices,
      //   skipDuplicates: true
      // });
    } catch (error) {
      console.error('Failed to save devices:', error);
    }
  }

  static startPeriodicSync() {
    // Set up periodic device synchronization
    setInterval(() => {
      this.synchronizeDevices();
    }, this.DEVICE_SYNC_INTERVAL);
  }
}
</boltAction>

