<boltAction type="file" filePath="app/services/security/authentication-manager.ts">
import { createCookieSessionStorage, redirect } from '@remix-run/node';
import bcrypt from 'bcryptjs';
import { v4 as uuidv4 } from 'uuid';

interface UserSession {
  id: string;
  email: string;
  role: 'user' | 'admin' | 'tester';
  permissions: string[];
}

export class AuthenticationManager {
  private static sessionStorage = createCookieSessionStorage({
    cookie: {
      name: '__session',
      httpOnly: true,
      maxAge: 60 * 60 * 24 * 7, // 1 week
      path: '/',
      sameSite: 'lax',
      secrets: [process.env.SESSION_SECRET || 'fallback_secret'],
      secure: process.env.NODE_ENV === 'production'
    }
  });

  static async createUserSession(user: UserSession, remember?: boolean) {
    const session = await this.sessionStorage.getSession();

    // Add multi-factor authentication token
    const mfaToken = this.generateMFAToken();

    session.set('user', {
      ...user,
      mfaToken,
      loginTimestamp: Date.now()
    });

    return redirect('/dashboard', {
      headers: {
        'Set-Cookie': await this.sessionStorage.commitSession(session, {
          maxAge: remember ? 60 * 60 * 24 * 30 : undefined
        })
      }
    });
  }

  static async requireAuthentication(request: Request, requiredRole?: UserSession['role']) {
    const session = await this.sessionStorage.getSession(
      request.headers.get('Cookie')
    );
    const user = session.get('user');

    if (!user) {
      throw redirect('/login', {
        headers: {
          'Set-Cookie': await this.sessionStorage.destroySession(session)
        }
      });
    }

    // Role-based access control
    if (requiredRole && user.role !== requiredRole) {
      throw redirect('/unauthorized', {
        headers: {
          'Set-Cookie': await this.sessionStorage.destroySession(session)
        }
      });
    }

    // Check session age and force re-authentication if too old
    const MAX_SESSION_AGE = 24 * 60 * 60 * 1000; // 24 hours
    if (Date.now() - user.loginTimestamp > MAX_SESSION_AGE) {
      throw redirect('/login', {
        headers: {
          'Set-Cookie': await this.sessionStorage.destroySession(session)
        }
      });
    }

    return user;
  }

  static async hashPassword(password: string): Promise<string> {
    return bcrypt.hash(password, 10);
  }

  static async verifyPassword(
    plainTextPassword: string, 
    hashedPassword: string
  ): Promise<boolean> {
    return bcrypt.compare(plainTextPassword, hashedPassword);
  }

  private static generateMFAToken(): string {
    return uuidv4();
  }

  static async generatePasswordResetToken(email: string): Promise<string> {
    const token = uuidv4();

    // Store token with expiration in database
    await this.storePasswordResetToken(email, token);

    return token;
  }

  private static async storePasswordResetToken(email: string, token: string) {
    // Implement database storage of reset token
