<boltAction type="file" filePath="app/services/ml/compatibility-optimizer.ts">
import * as tf from '@tensorflow/tfjs';
import { Device, DeviceTestResult } from '~/types/device';

interface CompatibilityFeature {
  deviceType: string;
  screenWidth: number;
  screenHeight: number;
  osVersion: string;
  browserType: string;
  previousTestResults: number[];
}

export class CompatibilityOptimizer {
  private model: tf.Sequential;

  constructor() {
    this.model = this.createModel();
  }

  private createModel(): tf.Sequential {
    const model = tf.sequential();

    // Input layer
    model.add(tf.layers.dense({
      inputShape: [6],
      units: 64,
      activation: 'relu'
    }));

    // Hidden layers
    model.add(tf.layers.dense({
      units: 32,
      activation: 'relu'
    }));
    model.add(tf.layers.dropout({ rate: 0.2 }));

    model.add(tf.layers.dense({
      units: 16,
      activation: 'relu'
    }));

    // Output layer
    model.add(tf.layers.dense({
      units: 1,
      activation: 'sigmoid'
    }));

    // Compile the model
    model.compile({
      optimizer: 'adam',
      loss: 'binaryCrossentropy',
      metrics: ['accuracy']
    });

    return model;
  }

  async trainModel(testResults: DeviceTestResult[]) {
    // Prepare training data
    const features: CompatibilityFeature[] = testResults.map(result => ({
      deviceType: result.device.type,
      screenWidth: result.screenSize.width,
      screenHeight: result.screenSize.height,
      osVersion: result.device.supportedOS[0]?.version || '',
      browserType: 'unknown', // Extend to capture actual browser
      previousTestResults: [
        result.performanceScore || 0,
        result.responsiveDesignScore || 0
      ]
    }));

    // Convert features to tensor
    const xs = tf.tensor2d(features.map(f => [
      this.encodeDeviceType(f.deviceType),
      f.screenWidth / 1000,
      f.screenHeight / 1000,
      this.encodeOSVersion(f.osVersion),
      this.encodeBrowserType(f.browserType),
      ...f.previousTestResults.map(r => r / 100)
    ]));

    // Labels (compatibility)
    const ys = tf.tensor1d(
      testResults.map(r => 
        r.compatibility === 'fully_compatible' ? 1 : 0
      )
    );

    // Train the model
    await this.model.fit(xs, ys, {
      epochs: 100,
      batchSize: 32,
      validationSplit: 0.2
    });
  }

  predictCompatibility(device: Device, previousResults: number[]): number {
    const inputTensor = tf.tensor2d([
      [
        this.encodeDeviceType(device.type),
        (device.screenSizes[0]?.width || 0) / 1000,
        (device.screenSizes[0]?.height || 0) / 1000,
        this.encodeOSVersion(device.supportedOS[0]?.version || ''),
        this.encodeBrowserType('unknown'),
        ...previousResults.map(r => r / 100)
      ]
    ]);

    const prediction = this.model.predict(inputTensor) as tf.Tensor;
    return prediction.dataSync()[0];
  }

  private encodeDeviceType(type: string): number {
    const types = ['phone', 'tablet', 'laptop', 'desktop'];
    return types.indexOf(type) / types.length;
  }

  private encodeOSVersion(version: string): number {
    const match = version.match(/\d+/);
    return match ? parseInt(match[0]) / 100 : 0;
  }

  private encodeBrowserType(browser: string): number {
    const browsers = ['chrome', 'firefox', 'safari', 'edge', 'unknown'];
    return browsers.indexOf(browser.toLowerCase()) / browsers.length;
  }

  async saveModel(path: string) {
    await this.model.save(path);
  }

  async loadModel(path: string) {
    this.model = await tf.loadLayersModel(path) as tf.Sequential;
    this.model.compile({
      optimizer: 'adam',
      loss: 'binaryCrossentropy',
      metrics: ['accuracy']
    });
  }
}

// Singleton instance
export const compatibilityOptimizer = new CompatibilityOptimizer();
</boltAction>

