import puppeteer from 'puppeteer';
import { Device, DeviceTestResult } from '~/types/device';
import { compatibilityOptimizer } from '../ml/compatibility-optimizer';

export class AdvancedTestEngine {
  static async performComprehensiveTest(
    url: string, 
    device: Device
  ): Promise<DeviceTestResult> {
    const browser = await puppeteer.launch({
      headless: true,
      args: ['--no-sandbox', '--disable-setuid-sandbox']
    });

    try {
      const page = await browser.newPage();

      // Set device emulation
      await page.emulate({
        viewport: {
          width: device.screenSizes[0]?.width || 1024,
          height: device.screenSizes[0]?.height || 768,
          deviceScaleFactor: 1
        },
        userAgent: device.userAgent || 'Mozilla/5.0'
      });

      // Performance and loading metrics
      const performanceMetrics = await this.measurePerformance(page, url);

      // Responsive design check
      const responsiveScore = await this.checkResponsiveDesign(page);

      // Accessibility analysis
      const accessibilityIssues = await this.analyzeAccessibility(page);

      // Predict compatibility using ML model
      const compatibilityPrediction = compatibilityOptimizer.predictCompatibility(
        device, 
        [performanceMetrics.overallScore, responsiveScore]
      );

      // Determine compatibility level
      const compatibility = this.determineCompatibilityLevel(compatibilityPrediction);

      return {
        url,
        device,
        screenSize: device.screenSizes[0] || { width: 0, height: 0 },
        compatibility,
        performanceScore: performanceMetrics.overallScore,
        responsiveDesignScore: responsiveScore,
        renderIssues: accessibilityIssues.map(issue => issue.message),
        testDate: new Date()
      };
    } catch (error) {
      console.error('Advanced testing error:', error);

      return {
        url,
        device,
        screenSize: device.screenSizes[0] || { width: 0, height: 0 },
        compatibility: 'incompatible',
        performanceScore: 0,
        responsiveDesignScore: 0,
        renderIssues: ['Failed to complete testing'],
        testDate: new Date()
      };
    } finally {
      await browser.close();
    }
  }

  private static async measurePerformance(page: puppeteer.Page, url: string) {
    await page.goto(url, { waitUntil: 'networkidle0' });

    // Collect various performance metrics
    const metrics = await page.metrics();
    const navigationTiming = await page.evaluate(() => performance.getEntriesByType('navigation')[0]);

    // Calculate overall performance score
    const overallScore = this.calculatePerformanceScore(metrics, navigationTiming);

    return {
      loadTime: navigationTiming.duration,
      domContentLoaded: navigationTiming.domContentLoadedEventEnd,
      overallScore
    };
  }

  private static calculatePerformanceScore(
    metrics: Record<string, number>, 
    navigationTiming: PerformanceNavigationTiming
  ): number {
    const scoreFactors = [
      1 - (navigationTiming.duration / 10000), // Load time (max 10s)
      1 - (metrics.TaskDuration / 1000), // Task duration
      1 - (metrics.JSHeapUsedSize / metrics.JSHeapTotalSize) // Memory efficiency
    ];

    const averageScore = scoreFactors.reduce((a, b) => a + b, 0) / scoreFactors.length;
    return Math.max(0, Math.min(100, averageScore * 100));
  }

  private static async checkResponsiveDesign(page: puppeteer.Page): Promise<number> {
    const viewports = [
      { width: 320, height: 568 },   // Mobile
      { width: 768, height: 1024 },  // Tablet
      { width: 1920, height: 1080 }  // Desktop
    ];

    let responsiveScore = 0;
    for (const viewport of viewports) {
      await page.setViewport(viewport);

      const layoutStability = await page.evaluate(() => {
        // Check if key elements are still visible and properly positioned
        const mainContent = document.querySelector('main, .main-content');
        return mainContent ? mainContent.getBoundingClientRect().width > 0 : false;
      });

      responsiveScore += layoutStability ? 33.33 : 0;
    }

    return responsiveScore;
  }

  private static async analyzeAccessibility(page: puppeteer.Page) {
    return page.evaluate(() => {
      const issues: { message: string }[] = [];

      // Check for alt text on images
      document.querySelectorAll('img:not([alt])').forEach(() => {
        issues.push({ message: 'Missing alt text on image' });
      });

      // Check color contrast
      document.querySelectorAll('*').forEach(el => {
        const style = window.getComputedStyle(el);
        const color = style.color;
        const backgroundColor = style.backgroundColor;

        if (!isGoodContrast(color, backgroundColor)) {
          issues.push({ message: 'Poor color contrast' });
        }
      });

      // Helper function for contrast check
      function isGoodContrast(color1: string, color2: string): boolean {
        return Math.abs(luminance(color1) - luminance(color2)) > 0.5;
      }

      function luminance(color: string): number {
        const rgb = color.match(/\d+/g)?.map(Number) || [0, 0, 0];
        const [r, g, b] = rgb.map(c => {
          c /= 255;
          return c <= 0.03928 
            ? c / 12.92 
            : Math.pow((c + 0.055) / 1.055, 2.4);
        });
        return 0.2126 * r + 0.7152 * g + 0.0722 * b;
      }

      return issues;
    });
  }

  private static determineCompatibilityLevel(
    compatibilityScore: number
  ): DeviceTestResult['compatibility'] {
    if (compatibilityScore > 0.7) return 'fully_compatible';
    if (compatibilityScore > 0.4) return 'partially_compatible';
    return 'incompatible';
  }
}
</boltAction>

