import axios from 'axios';
import { Device, DeviceTestResult } from '~/types/device';

export interface IntegrationConfig {
  name: string;
  apiKey: string;
  baseUrl: string;
}

export class ThirdPartyIntegrationService {
  private static integrations: Record<string, IntegrationConfig> = {
    browserstack: {
      name: 'BrowserStack',
      apiKey: process.env.BROWSERSTACK_API_KEY || '',
      baseUrl: 'https://api.browserstack.com/automate'
    },
    saucelabs: {
      name: 'Sauce Labs',
      apiKey: process.env.SAUCELABS_API_KEY || '',
      baseUrl: 'https://api.us-west-1.saucelabs.com/v1'
    }
  };

  static async syncDevices(integrationName: string): Promise<Device[]> {
    const integration = this.integrations[integrationName];
    if (!integration) {
      throw new Error(`Integration ${integrationName} not found`);
    }

    try {
      const response = await axios.get(`${integration.baseUrl}/devices`, {
        headers: {
          'Authorization': `Bearer ${integration.apiKey}`,
          'Accept': 'application/json'
        }
      });

      return this.transformDevices(response.data, integrationName);
    } catch (error) {
      console.error(`Device sync error for ${integrationName}:`, error);
      return [];
    }
  }

  static async submitTestResult(
    integrationName: string, 
    testResult: DeviceTestResult
  ): Promise<boolean> {
    const integration = this.integrations[integrationName];
    if (!integration) {
      throw new Error(`Integration ${integrationName} not found`);
    }

    try {
      await axios.post(`${integration.baseUrl}/test-results`, 
        this.transformTestResult(testResult),
        {
          headers: {
            'Authorization': `Bearer ${integration.apiKey}`,
            'Content-Type': 'application/json'
          }
        }
      );

      return true;
    } catch (error) {
      console.error(`Test result submission error for ${integrationName}:`, error);
      return false;
    }
  }

  private static transformDevices(
    rawDevices: any[], 
    integrationName: string
  ): Device[] {
    return rawDevices.map(device => ({
      id: `${integrationName}_${device.id}`,
      name: device.name,
      type: this.normalizeDeviceType(device.type),
      manufacturer: device.manufacturer,
      screenSizes: device.screenSizes || [],
      supportedOS: device.supportedOS || [],
      isActive: true,
      lastUpdated: new Date(),
      userAgent: device.userAgent
    }));
  }

  private static transformTestResult(
    testResult: DeviceTestResult
  ): Record<string, any> {
    return {
      url: testResult.url,
      device: {
        id: testResult.device.id,
        name: testResult.device.name
      },
      compatibility: testResult.compatibility,
      performanceScore: testResult.performanceScore,
      responsiveDesignScore: testResult.responsiveDesignScore,
      renderIssues: testResult.renderIssues,
      testDate: testResult.testDate.toISOString()
    };
  }

  private static normalizeDeviceType(type: string): Device['type'] {
    const typeMap: Record<string, Device['type']> = {
      'mobile': 'phone',
      'smartphone': 'phone',
      'tablet': 'tablet',
      'laptop': 'laptop',
      'desktop': 'desktop'
    };
    return typeMap[type.toLowerCase()] || 'phone';
  }
}
</boltAction>

