import winston from 'winston';
import { ElasticsearchTransport } from 'winston-elasticsearch';

export interface LogEntry {
  level: 'error' | 'warn' | 'info' | 'debug';
  message: string;
  metadata?: Record<string, any>;
  timestamp?: Date;
  traceId?: string;
}

export class ComprehensiveLogger {
  private static instance: ComprehensiveLogger;
  private logger: winston.Logger;

  private constructor() {
    this.logger = winston.createLogger({
      level: process.env.LOG_LEVEL || 'info',
      format: winston.format.combine(
        winston.format.timestamp(),
        winston.format.json()
      ),
      defaultMeta: { service: 'mobile-testing-platform' },
      transports: this.createTransports()
    });
  }

  public static getInstance(): ComprehensiveLogger {
    if (!ComprehensiveLogger.instance) {
      ComprehensiveLogger.instance = new ComprehensiveLogger();
    }
    return ComprehensiveLogger.instance;
  }

  private createTransports(): winston.transport[] {
    const transports: winston.transport[] = [
      // Console transport
      new winston.transports.Console({
        format: winston.format.simple()
      })
    ];

    // Elasticsearch transport for production
    if (process.env.NODE_ENV === 'production') {
      transports.push(
        new ElasticsearchTransport({
          level: 'info',
          clientOpts: {
            node: process.env.ELASTICSEARCH_URL || 'http://localhost:9200',
            auth: {
              username: process.env.ELASTICSEARCH_USERNAME || '',
              password: process.env.ELASTICSEARCH_PASSWORD || ''
            }
          },
          indexPrefix: 'mobile-testing-logs',
          transformer: (logData) => {
            const { message, level, metadata, timestamp } = logData;
            return {
              message,
              level,
              ...metadata,
              timestamp: timestamp || new Date()
            };
          }
        })
      );
    }

    return transports;
  }

  log(entry: LogEntry) {
    const { level, message, metadata, traceId } = entry;

    // Generate trace ID if not provided
    const currentTraceId = traceId || this.generateTraceId();

    this.logger.log({
      level,
      message,
      metadata: {
        ...metadata,
        traceId: currentTraceId
      }
    });
  }

  error(message: string, metadata?: Record<string, any>) {
    this.log({
      level: 'error',
      message,
      metadata
    });
  }

  warn(message: string, metadata?: Record<string, any>) {
    this.log({
      level: 'warn',
      message,
      metadata
    });
  }

  info(message: string, metadata?: Record<string, any>) {
    this.log({
      level: 'info',
      message,
      metadata
    });
  }

  debug(message: string, metadata?: Record<string, any>) {
    this.log({
      level: 'debug',
      message,
      metadata
    });
  }

  private generateTraceId(): string {
    return `trace_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  // Advanced log querying method
  async queryLogs(options: {
    level?: LogEntry['level'];
    from?: Date;
    to?: Date;
    limit?: number;
  }) {
    // In a real-world scenario, this would query Elasticsearch
    // For this example, we'll simulate a query
    return {
      logs: [],
      total: 0
    };
  }
}

// Singleton instance
export const comprehensiveLogger = ComprehensiveLogger.getInstance();
</boltAction>

