import PDFDocument from 'pdfkit';
import { DeviceTestResult } from '~/types/device';
import { TestResultModel } from '~/models/test-result.server';

export interface ReportConfig {
  format: 'pdf' | 'html' | 'json';
  includeDetails?: boolean;
  dateRange?: {
    start: Date;
    end: Date;
  };
}

export class ComprehensiveReportGenerator {
  static async generateReport(
    config: ReportConfig
  ): Promise<Buffer | string> {
    // Fetch test results based on configuration
    const testResults = await this.fetchTestResults(config);

    // Generate report based on format
    switch (config.format) {
      case 'pdf':
        return this.generatePDFReport(testResults, config);
      case 'html':
        return this.generateHTMLReport(testResults, config);
      case 'json':
      default:
        return this.generateJSONReport(testResults, config);
    }
  }

  private static async fetchTestResults(
    config: ReportConfig
  ): Promise<DeviceTestResult[]> {
    // Use TestResultModel to fetch results
    return TestResultModel.getTestResults({
      dateRange: config.dateRange
    });
  }

  private static generatePDFReport(
    testResults: DeviceTestResult[], 
    config: ReportConfig
  ): Buffer {
    const doc = new PDFDocument();
    const buffers: Buffer[] = [];

    // Collect buffer chunks
    doc.on('data', buffers.push.bind(buffers));

    // PDF Generation
    doc.fontSize(25).text('Mobile Testing Compatibility Report', { align: 'center' });

    // Summary Statistics
    const summary = this.generateSummaryStatistics(testResults);
    doc.fontSize(12)
      .text(`Total Tests: ${summary.totalTests}`, { align: 'left' })
      .text(`Fully Compatible: ${summary.fullyCompatiblePercentage.toFixed(2)}%`, { align: 'left' })
      .text(`Partially Compatible: ${summary.partiallyCompatiblePercentage.toFixed(2)}%`, { align: 'left' })
      .text(`Incompatible: ${summary.incompatiblePercentage.toFixed(2)}%`, { align: 'left' });

    // Detailed Results
    if (config.includeDetails) {
      testResults.forEach(result => {
        doc.text(`URL: ${result.url}`)
          .text(`Device: ${result.device.name}`)
          .text(`Compatibility: ${result.compatibility}`)
          .text(`Performance Score: ${result.performanceScore?.toFixed(2)}`)
          .moveDown();
      });
    }

    doc.end();

    // Wait for PDF generation
    return Buffer.concat(buffers);
  }

  private static generateHTMLReport(
    testResults: DeviceTestResult[], 
    config: ReportConfig
  ): string {
    const summary = this.generateSummaryStatistics(testResults);

    let html = `
      <!DOCTYPE html>
      <html>
      <head>
        <title>Mobile Testing Compatibility Report</title>
        <style>
          body { font-family: Arial, sans-serif; }
          .summary { margin-bottom: 20px; }
          table { width: 100%; border-collapse: collapse; }
          th, td { border: 1px solid #ddd; padding: 8px; }
        </style>
      </head>
      <body>
        <h1>Mobile Testing Compatibility Report</h1>

        <div class="summary">
          <h2>Summary Statistics</h2>
          <p>Total Tests: ${summary.totalTests}</p>
          <p>Fully Compatible: ${summary.fullyCompatiblePercentage.toFixed(2)}%</p>
          <p>Partially Compatible: ${summary.partiallyCompatiblePercentage.toFixed(2)}%</p>
          <p>Incompatible: ${summary.incompatiblePercentage.toFixed(2)}%</p>
        </div>
    `;

    if (config.includeDetails) {
      html += `
        <table>
          <thead>
            <tr>
              <th>URL</th>
              <th>Device</th>
              <th>Compatibility</th>
              <th>Performance Score</th>
            </tr>
          </thead>
          <tbody>
            ${testResults.map(result => `
              <tr>
                <td>${result.url}</td>
                <td>${result.device.name}</td>
                <td>${result.compatibility}</td>
                <td>${result.performanceScore?.toFixed(2)}</td>
              </tr>
            `).join('')}
          </tbody>
        </table>
      `;
    }

    html += `
      </body>
      </html>
    `;

    return html;
  }

  private static generateJSONReport(
    testResults: DeviceTestResult[], 
    config: ReportConfig
  ): string {
    const summary = this.generateSummaryStatistics(testResults);

    return JSON.stringify({
      summary,
      testResults: config.includeDetails ? testResults : undefined
    }, null, 2);
  }

  private static generateSummaryStatistics(testResults: DeviceTestResult[]) {
    const totalTests = testResults.length;

    const compatibilityCounts = {
      fullyCompatible: 0,
      partiallyCompatible: 0,
      incompatible: 0
    };

    testResults.forEach(result => {
      switch (result.compatibility) {
        case 'fully_compatible':
          compatibilityCounts.fullyCompatible++;
          break;
        case 'partially_compatible':
          compatibilityCounts.partiallyCompatible++;
          break;
        case 'incompatible':
          compatibilityCounts.incompatible++;
          break;
      }
    });

    return {
      totalTests,
      fullyCompatiblePercentage: 
        (compatibilityCounts.fullyCompatible / totalTests) * 100,
      partiallyCompatiblePercentage: 
        (compatibilityCounts.partiallyCompatible / totalTests) * 100,
      incompatiblePercentage: 
        (compatibilityCounts.incompatible / totalTests) * 100
    };
  }
}
</boltAction>

