import { ActionFunctionArgs } from '@remix-run/node';
import { ComprehensiveReportGenerator } from '~/services/reporting/comprehensive-report-generator';
import { AuthenticationManager } from '~/services/security/authentication-manager';
import { comprehensiveLogger } from '~/services/monitoring/comprehensive-logger';

export async function action({ request }: ActionFunctionArgs) {
  // Authenticate and validate request
  const user = await AuthenticationManager.requireAuthentication(request, 'admin');

  if (request.method !== 'POST') {
    return new Response('Method Not Allowed', { status: 405 });
  }

  try {
    const { 
      format = 'pdf', 
      includeDetails = true, 
      dateRange 
    } = await request.json();

    // Generate report
    const report = await ComprehensiveReportGenerator.generateReport({
      format,
      includeDetails,
      dateRange
    });

    // Log report generation
    comprehensiveLogger.info('Report generated', {
      userId: user.id,
      format,
      includeDetails
    });

    // Return report based on format
    return new Response(report, {
      status: 200,
      headers: {
        'Content-Type': format === 'pdf' 
          ? 'application/pdf' 
          : format === 'html' 
            ? 'text/html' 
            : 'application/json',
        'Content-Disposition': `attachment; filename=mobile_testing_report.${format}`
      }
    });
  } catch (error) {
    // Log error
    comprehensiveLogger.error('Report generation failed', {
      error: error instanceof Error ? error.message : 'Unknown error'
    });

    return new Response(JSON.stringify({ 
      error: 'Report generation failed',
      details: error instanceof Error ? error.message : 'Unknown error'
    }), { 
      status: 500,
      headers: { 'Content-Type': 'application/json' }
    });
  }
}
</boltAction>
