import { useState, useEffect } from 'react';
import { DeviceService } from '~/services/device-service';
import { EnhancedTestingService } from '~/services/testing-service.enhanced';
import { Device, ScreenSize, DeviceTestResult } from '~/types/device';

export default function Index() {
  const [devices, setDevices] = useState<Device[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<Device | null>(null);
  const [selectedScreenSize, setSelectedScreenSize] = useState<ScreenSize | null>(null);
  const [url, setUrl] = useState('');
  const [testResults, setTestResults] = useState<DeviceTestResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    async function loadDevices() {
      const fetchedDevices = await DeviceService.syncDeviceCatalog();
      setDevices(fetchedDevices);
    }
    loadDevices();
  }, []);

  const handleTestWebsite = async () => {
    if (!url || !selectedDevice || !selectedScreenSize) {
      alert('Please complete all selections');
      return;
    }

    setIsLoading(true);
    try {
      const result = await EnhancedTestingService.testWebsiteCompatibility(
        url, 
        selectedDevice, 
        selectedScreenSize
      );

      setTestResults(prev => [result, ...prev]);
    } catch (error) {
      console.error('Testing failed', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-indigo-600 text-white py-6 shadow-md">
        <div className="container mx-auto px-4">
          <h1 className="text-3xl font-bold">Mobile Device Testing Platform</h1>
          <p className="mt-2 text-indigo-200">
            Test your website's compatibility across multiple devices
          </p>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="grid md:grid-cols-2 gap-8">
          {/* Device and Testing Configuration */}
          <div className="bg-white shadow-lg rounded-lg p-6">
            <h2 className="text-2xl font-semibold mb-6 text-gray-800">
              Website Compatibility Test
            </h2>

            {/* URL Input */}
            <div className="mb-4">
              <label 
                htmlFor="website-url" 
                className="block text-sm font-medium text-gray-700 mb-2"
              >
                Website URL
              </label>
              <input 
                id="website-url"
                type="url" 
                placeholder="Enter website URL (e.g., https://example.com)" 
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
            </div>

            {/* Device Selection */}
            <div className="mb-4">
              <label 
                htmlFor="device-select" 
                className="block text-sm font-medium text-gray-700 mb-2"
              >
                Select Device
              </label>
              <select 
                id="device-select"
                value={selectedDevice?.id || ''}
                onChange={(e) => {
                  const device = devices.find(d => d.id === e.target.value);
                  setSelectedDevice(device || null);
                  setSelectedScreenSize(device?.screenSizes[0] || null);
                }}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                <option value="">Choose a Device</option>
                {devices.map(device => (
                  <option key={device.id} value={device.id}>
                    {device.name} - {device.manufacturer}
                  </option>
                ))}
              </select>
            </div>

            {/* Screen Size Selection */}
            {selectedDevice && (
              <div className="mb-4">
                <label 
                  htmlFor="screen-size" 
                  className="block text-sm font-medium text-gray-700 mb-2"
                >
                  Screen Size
                </label>
                <select 
                  id="screen-size"
                  value={selectedScreenSize ? `${selectedScreenSize.width}x${selectedScreenSize.height}` : ''}
                  onChange={(e) => {
                    const [width, height] = e.target.value.split('x').map(Number);
                    setSelectedScreenSize({ width, height });
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  {selectedDevice.screenSizes.map(size => (
                    <option 
                      key={`${size.width}x${size.height}`} 
                      value={`${size.width}x${size.height}`}
                    >
                      {size.width} x {size.height}
                    </option>
                  ))}
                </select>
              </div>
            )}

            {/* Test Button */}
            <button 
              onClick={handleTestWebsite}
              disabled={isLoading || !url || !selectedDevice || !selectedScreenSize}
              className={`
                w-full py-3 rounded-md text-white font-semibold transition-colors duration-300
                ${isLoading || !url || !selectedDevice || !selectedScreenSize
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500'
                }
              `}
            >
              {isLoading ? 'Testing...' : 'Run Compatibility Test'}
            </button>
          </div>

          {/* Test Results */}
          <div className="bg-white shadow-lg rounded-lg p-6">
            <h2 className="text-2xl font-semibold mb-6 text-gray-800">
              Test Results
            </h2>

            {testResults.length === 0 ? (
              <div className="text-center text-gray-500">
                <p>No test results yet. Run a test to see compatibility details.</p>
              </div>
            ) : (
              <div className="space-y-4">
                {testResults.map((result, index) => (
                  <div 
                    key={index} 
                    className={`
                      p-4 rounded-lg shadow-md
                      ${result.compatibility === 'fully_compatible' 
                        ? 'bg-green-50 border-l-4 border-green-500' 
                        : result.compatibility === 'partially_compatible' 
                          ? 'bg-yellow-50 border-l-4 border-yellow-500' 
                          : 'bg-red-50 border-l-4 border-red-500'
                      }
                    `}
                  >
                    <div className="flex justify-between items-center mb-2">
                      <h3 className="font-semibold text-gray-800">
                        {result.device.name}
                      </h3>
                      <span 
                        className={`
                          px-2 py-1 rounded-full text-xs font-medium
                          ${result.compatibility === 'fully_compatible' 
                            ? 'bg-green-100 text-green-800' 
                            : result.compatibility === 'partially_compatible' 
                              ? 'bg-yellow-100 text-yellow-800' 
                              : 'bg-red-100 text-red-800'
                          }
                        `}
                      >
                        {result.compatibility.replace('_', ' ')}
                      </span>
                    </div>
                    <p className="text-sm text-gray-600 mb-2">
                      URL: {result.url}
                    </p>
                    <div className="grid grid-cols-2 gap-2">
                      <div>
                        <p className="text-xs text-gray-500">Performance</p>
                        <p className="font-medium">
                          {result.performanceScore?.toFixed(2) || 'N/A'}%
                        </p>
                      </div>
                      <div>
                        <p className="text-xs text-gray-500">Responsive Design</p>
                        <p className="font-medium">
                          {result.responsiveDesignScore?.toFixed(2) || 'N/A'}%
                        </p>
                      </div>
                    </div>
                    {result.renderIssues && result.renderIssues.length > 0 && (
                      <div className="mt-2">
                        <p className="text-xs text-gray-500 mb-1">Issues:</p>
                        <ul className="list-disc list-inside text-sm text-gray-700">
                          {result.renderIssues.map((issue, i) => (
                            <li key={i}>{issue}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-gray-800 text-white py-6">
        <div className="container mx-auto px-4 text-center">
          <p>&copy; 2023 Mobile Testing Platform. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}
</boltAction>

