import { LoaderFunctionArgs } from '@remix-run/node';
import { useLoaderData } from '@remix-run/react';
import { requireUserId } from '~/session.server';
import { TestResultModel } from '~/models/test-result.server';
import { UserModel } from '~/models/user.server';

export async function loader({ request }: LoaderFunctionArgs) {
  const userId = await requireUserId(request);

  const [testHistory, userData] = await Promise.all([
    TestResultModel.getUserTestResults(userId),
    UserModel.getUserProfile(userId)
  ]);

  return { testHistory, userData };
}

export default function Dashboard() {
  const { testHistory, userData } = useLoaderData<typeof loader>();

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-indigo-600 text-white py-6 shadow-md">
        <div className="container mx-auto px-4 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold">Dashboard</h1>
            <p className="mt-2 text-indigo-200">
              Welcome back, {userData.name}
            </p>
          </div>
          <a 
            href="/test" 
            className="bg-white text-indigo-600 px-4 py-2 rounded-md hover:bg-indigo-50"
          >
            New Test
          </a>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="grid md:grid-cols-3 gap-8">
          {/* User Profile Summary */}
          <div className="bg-white shadow-lg rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Profile Overview
            </h2>
            <div className="space-y-2">
              <p><strong>Email:</strong> {userData.email}</p>
              <p><strong>Total Tests:</strong> {testHistory.length}</p>
              <p>
                <strong>Last Test:</strong> 
                {testHistory.length > 0 
                  ? new Date(testHistory[0].testDate).toLocaleString() 
                  : 'No tests yet'}
              </p>
            </div>
          </div>

          {/* Compatibility Statistics */}
          <div className="bg-white shadow-lg rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Compatibility Overview
            </h2>
            <div className="space-y-2">
              {['fully_compatible', 'partially_compatible', 'incompatible'].map(status => {
                const count = testHistory.filter(
                  test => test.compatibility === status
                ).length;
                const percentage = (count / testHistory.length) * 100 || 0;

                return (
                  <div key={status} className="flex items-center">
                    <div 
                      className={`
                        w-full bg-gray-200 rounded-full h-2.5 mr-2
                        ${status === 'fully_compatible' ? 'bg-green-200' : 
                          status === 'partially_compatible' ? 'bg-yellow-200' : 
                          'bg-red-200'}
                      `}
                    >
                      <div 
                        className={`
                          h-2.5 rounded-full
                          ${status === 'fully_compatible' ? 'bg-green-600' : 
                            status === 'partially_compatible' ? 'bg-yellow-600' : 
                            'bg-red-600'}
                        `}
                        style={{ width: `${percentage}%` }}
                      ></div>
                    </div>
                    <span className="text-sm text-gray-600">
                      {percentage.toFixed(1)}%
                    </span>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Recent Test History */}
          <div className="bg-white shadow-lg rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Recent Tests
            </h2>
            {testHistory.length === 0 ? (
              <p className="text-gray-500 text-center">
                No test history available
              </p>
            ) : (
              <div className="space-y-4">
                {testHistory.slice(0, 5).map(test => (
                  <div 
                    key={test.id} 
                    className={`
                      p-3 rounded-lg
                      ${test.compatibility === 'fully_compatible' 
                        ? 'bg-green-50 border-l-4 border-green-500' 
                        : test.compatibility === 'partially_compatible' 
                          ? 'bg-yellow-50 border-l-4 border-yellow-500' 
                          : 'bg-red-50 border-l-4 border-red-500'
                      }
                    `}
                  >
                    <div className="flex justify-between items-center">
                      <p className="text-sm text-gray-700 truncate">
                        {test.url}
                      </p>
                      <span 
                        className={`
                          px-2 py-1 rounded-full text-xs font-medium
                          ${test.compatibility === 'fully_compatible' 
                            ? 'bg-green-100 text-green-800' 
                            : test.compatibility === 'partially_compatible' 
                              ? 'bg-yellow-100 text-yellow-800' 
                              : 'bg-red-100 text-red-800'
                          }
                        `}
                      >
                        {test.compatibility.replace('_', ' ')}
                      </span>
                    </div>
                    <p className="text-xs text-gray-500 mt-1">
                      {new Date(test.testDate).toLocaleString()}
                    </p>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}
</boltAction>

<boltAction type="file" filePath="app/models/user.server.ts">
import { prisma } from '~/db.server';
import bcrypt from 'bcryptjs';

export interface UserCreateInput {
  email: string;
  password: string;
  name?: string;
}

export class UserModel {
  static async create(data: UserCreateInput) {
    // Check if user already exists
    const existingUser = await prisma.user.findUnique({
      where: { email: data.email }
    });

    if (existingUser) {
      throw new Error('User with this email already exists');
    }

    const hashedPassword = await bcrypt.hash(data.password, 10);

    return prisma.user.create({
      data: {
        email: data.email,
        password: hashedPassword,
        name: data.name
      },
      select: {
        id: true,
        email: true,
        name: true
      }
    });
  }

  static async getUserProfile(userId: string) {
    return prisma.user.findUnique({
      where: { id: userId },
      select: {
        id: true,
        email: true,
        name: true,
        createdAt: true
      }
    });
  }

  static async updateProfile(
    userId: string, 
    updateData: { 
      name?: string, 
      email?: string 
    }
  ) {
    return prisma.user.update({
      where: { id: userId },
      data: updateData,
      select: {
        id: true,
        email: true,
        name: true
      }
    });
  }
}
</boltAction>

<boltAction type="file" filePath="app/models/test-result.server.ts">
import { prisma } from '~/db.server';
import { DeviceTestResult } from '~/types/device';

export class TestResultModel {
  static async createTestResult(
    result: Omit<DeviceTestResult, 'id'>, 
    userId?: string
  ) {
    return prisma.testResult.create({
      data: {
        url: result.url,
        deviceId: result.device.id,
        userId,
        screenWidth: result.screenSize.width,
        screenHeight: result.screenSize.height,
        compatibility: result.compatibility,
        performanceScore: result.performanceScore,
        responsiveDesignScore: result.responsiveDesignScore,
        renderIssues: result.renderIssues || [],
        testDate: result.testDate
      }
    });
  }

  static async getUserTestResults(userId: string) {
    return prisma.testResult.findMany({
      where: { userId },
      orderBy: { testDate: 'desc' },
      include: {
        device: true
      }
    });
  }

  static async getTestResults(options?: {
    dateRange?: { start: Date; end: Date }
  }) {
    return prisma.testResult.findMany({
      where: {
        testDate: options?.dateRange ? {
          gte: options.dateRange.start,
          lte: options.dateRange.end
        } : undefined
      },
      orderBy: { testDate: 'desc' },
      include: {
        device: true,
        user: {
          select: {
            id: true,
            email: true,
            name: true
          }
        }
      }
    });
  }
}
</boltAction>

