import { prisma } from '~/db.server';
import { DeviceTestResult } from '~/types/device';

export class TestResultModel {
  static async createTestResult(
    result: Omit<DeviceTestResult, 'id'>, 
    userId?: string
  ) {
    return prisma.testResult.create({
      data: {
        url: result.url,
        deviceId: result.device.id,
        userId,
        screenWidth: result.screenSize.width,
        screenHeight: result.screenSize.height,
        compatibility: result.compatibility,
        performanceScore: result.performanceScore,
        responsiveDesignScore: result.responsiveDesignScore,
        renderIssues: result.renderIssues || [],
        testDate: result.testDate
      }
    });
  }

  static async getUserTestResults(userId: string) {
    return prisma.testResult.findMany({
      where: { userId },
      orderBy: { testDate: 'desc' },
      include: {
        device: true
      }
    });
  }

  static async getTestResults(options?: {
    dateRange?: { start: Date; end: Date }
  }) {
    return prisma.testResult.findMany({
      where: {
        testDate: options?.dateRange ? {
          gte: options.dateRange.start,
          lte: options.dateRange.end
        } : undefined
      },
      orderBy: { testDate: 'desc' },
      include: {
        device: true,
        user: {
          select: {
            id: true,
            email: true,
            name: true
          }
        }
      }
    });
  }
}
</boltAction>

