datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

generator client {
  provider = "prisma-client-js"
}

model User {
  id        String   @id @default(cuid())
  email     String   @unique
  password  String
  name      String?
  createdAt DateTime @default(now())

  testResults TestResult[]
}

model Device {
  id             String   @id @default(cuid())
  name           String
  type           String
  manufacturer   String
  screenSizes    Json
  supportedOS    Json
  isActive       Boolean  @default(true)
  lastUpdated    DateTime @default(now())
  userAgent      String?

  testResults    TestResult[]
}

model TestResult {
  id                      String   @id @default(cuid())
  url                     String
  deviceId                String
  device                  Device   @relation(fields: [deviceId], references: [id])
  userId                  String?
  user                    User?    @relation(fields: [userId], references: [id])

  screenWidth             Int
  screenHeight            Int
  compatibility           String
  performanceScore        Float?
  responsiveDesignScore   Float?
  renderIssues            String[]
  testDate                DateTime @default(now())

  @@index([userId])
  @@index([deviceId])
}
