import { ComprehensiveReportGenerator } from '~/services/reporting/comprehensive-report-generator';

export default function ReportsPage() {
  const [reportFormat, setReportFormat] = useState<'pdf' | 'html' | 'json'>('pdf');
  const [dateRange, setDateRange] = useState({
    start: new Date(new Date().setMonth(new Date().getMonth() - 1)),
    end: new Date()
  });
  const [includeDetails, setIncludeDetails] = useState(true);

  const handleGenerateReport = async () => {
    try {
      const report = await ComprehensiveReportGenerator.generateReport({
        format: reportFormat,
        includeDetails,
        dateRange
      });

      // Create a blob and trigger download
      const blob = new Blob([report], { 
        type: reportFormat === 'pdf' 
          ? 'application/pdf' 
          : reportFormat === 'html' 
            ? 'text/html' 
            : 'application/json' 
      });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `mobile_testing_report.${reportFormat}`;
      a.click();
      window.URL.revokeObjectURL(url);
