// Simulated testing logic to avoid external API calls
export function simulateWebsiteTesting(url, device, screenSize) {
  // Simulate a testing process
  const performanceScore = Math.random() * 100;
  const responsiveScore = Math.random() * 100;

  // Generate potential issues
  const issues = [];
  if (performanceScore < 50) issues.push('Slow page load');
  if (responsiveScore < 50) issues.push('Poor responsive design');

  return {
    url,
    device,
    screenSize,
    performanceScore,
    responsiveScore,
    compatibility: performanceScore > 70 && responsiveScore > 70 
      ? 'Fully Compatible' 
      : performanceScore > 40 && responsiveScore > 40 
        ? 'Partially Compatible' 
        : 'Incompatible',
    issues,
    timestamp: new Date().toISOString()
  };
}

// Local storage based result tracking
export function saveTestResult(result) {
  const results = JSON.parse(localStorage.getItem('testResults') || '[]');
  results.unshift(result);
  localStorage.setItem('testResults', JSON.stringify(results.slice(0, 50)));
}

export function getTestResults() {
  return JSON.parse(localStorage.getItem('testResults') || '[]');
}
</boltAction>

