import React, { useState, useEffect } from 'react';
import { 
  DEVICE_CATALOG, 
  getDeviceTypes, 
  getDevicesByType 
} from './data/devices';
import { 
  simulateWebsiteTesting, 
  saveTestResult, 
  getTestResults 
} from './utils/testing-simulator';
import { AnalyticsModal } from './components/AnalyticsModal';

function App() {
  const [deviceTypes, setDeviceTypes] = useState([]);
  const [selectedType, setSelectedType] = useState('');
  const [devices, setDevices] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState(null);
  const [url, setUrl] = useState('');
  const [testResults, setTestResults] = useState([]);
  const [showAnalytics, setShowAnalytics] = useState(false);

  // Initialize device types and results
  useEffect(() => {
    setDeviceTypes(getDeviceTypes());
    setTestResults(getTestResults());
  }, []);

  // Update devices when type changes
  useEffect(() => {
    if (selectedType) {
      setDevices(getDevicesByType(selectedType));
    } else {
      setDevices([]);
    }
  }, [selectedType]);

  const handleTest = () => {
    if (url && selectedDevice) {
      // Select a random screen size for the device
      const screenSize = selectedDevice.screenSizes[
        Math.floor(Math.random() * selectedDevice.screenSizes.length)
      ];

      // Simulate testing
      const result = simulateWebsiteTesting(url, selectedDevice, screenSize);

      // Save and update results
      saveTestResult(result);
      setTestResults(prevResults => [result, ...prevResults]);
    }
  };

  const clearResults = () => {
    localStorage.removeItem('testResults');
    setTestResults([]);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto bg-white shadow-md rounded-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-indigo-600">
            Mobile Device Compatibility Tester
          </h1>
          {testResults.length > 0 && (
            <div className="flex space-x-2">
              <button 
                onClick={() => setShowAnalytics(true)}
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700"
              >
                View Analytics
              </button>
              <button 
                onClick={clearResults}
                className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
              >
                Clear Results
              </button>
            </div>
          )}
        </div>

        {/* Testing Configuration */}
        <div className="grid md:grid-cols-2 gap-6">
          <div className="space-y-4">
            <input 
              type="url" 
              placeholder="Enter website URL" 
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />

            <select 
              value={selectedType}
              onChange={(e) => setSelectedType(e.target.value)}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              <option value="">Select Device Type</option>
              {deviceTypes.map(type => (
                <option key={type} value={type}>
                  {type.charAt(0).toUpperCase() + type.slice(1)}
                </option>
              ))}
            </select>

            <select 
              value={selectedDevice?.id || ''}
              onChange={(e) => {
                const device = devices.find(d => d.id === e.target.value);
                setSelectedDevice(device);
              }}
              disabled={!selectedType}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:opacity-50"
            >
              <option value="">Select a Device</option>
              {devices.map(device => (
                <option key={device.id} value={device.id}>
                  {device.name} ({device.manufacturer})
                </option>
              ))}
            </select>

            <button 
              onClick={handleTest}
              disabled={!url || !selectedDevice}
              className={`
                w-full py-2 rounded-md text-white font-semibold
                ${!url || !selectedDevice 
                  ? 'bg-gray-400 cursor-not-allowed' 
                  : 'bg-indigo-600 hover:bg-indigo-700'}
              `}
            >
              Run Compatibility Test
            </button>
          </div>

          {/* Test Results */}
          <div>
            <h2 className="text-xl font-semibold mb-4 text-gray-700">
              Recent Test Results
            </h2>
            <div className="space-y-2 max-h-[400px] overflow-y-auto">
              {testResults.slice(0, 5).map((result, index) => (
                <div 
                  key={index} 
                  className={`
                    p-3 rounded-md
                    ${result.compatibility === 'Fully Compatible' 
                      ? 'bg-green-50 border-l-4 border-green-500' 
                      : result.compatibility === 'Partially Compatible'
                        ? 'bg-yellow-50 border-l-4 border-yellow-500'
                        : 'bg-red-50 border-l-4 border-red-500'}
                  `}
                >
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">{result.url}</span>
                    <span 
                      className={`
                        px-2 py-1 rounded-full text-xs
