// Advanced device and compatibility analysis utilities
export function analyzeCompatibilityTrends(testResults) {
  // Aggregate compatibility statistics
  const compatibilityCounts = {
    'Fully Compatible': 0,
    'Partially Compatible': 0,
    'Incompatible': 0
  };

  const devicePerformance = {};
  const manufacturerPerformance = {};

  testResults.forEach(result => {
    // Count compatibility levels
    compatibilityCounts[result.compatibility]++;

    // Track device performance
    const deviceKey = result.device.id;
    if (!devicePerformance[deviceKey]) {
      devicePerformance[deviceKey] = {
        name: result.device.name,
        totalTests: 0,
        averagePerformance: 0,
        compatibilitySummary: {
          'Fully Compatible': 0,
          'Partially Compatible': 0,
          'Incompatible': 0
        }
      };
    }

    devicePerformance[deviceKey].totalTests++;
    devicePerformance[deviceKey].averagePerformance += result.performanceScore;
    devicePerformance[deviceKey].compatibilitySummary[result.compatibility]++;

    // Track manufacturer performance
    const manufacturerKey = result.device.manufacturer;
    if (!manufacturerPerformance[manufacturerKey]) {
      manufacturerPerformance[manufacturerKey] = {
        totalTests: 0,
        averagePerformance: 0,
        compatibilitySummary: {
          'Fully Compatible': 0,
          'Partially Compatible': 0,
          'Incompatible': 0
        }
      };
    }

    manufacturerPerformance[manufacturerKey].totalTests++;
    manufacturerPerformance[manufacturerKey].averagePerformance += result.performanceScore;
    manufacturerPerformance[manufacturerKey].compatibilitySummary[result.compatibility]++;
  });

  // Calculate averages and percentages
  Object.values(devicePerformance).forEach(device => {
    device.averagePerformance /= device.totalTests;
  });

  Object.values(manufacturerPerformance).forEach(manufacturer => {
    manufacturer.averagePerformance /= manufacturer.totalTests;
  });

  return {
    overallCompatibility: {
      total: testResults.length,
      percentages: Object.fromEntries(
        Object.entries(compatibilityCounts).map(([key, value]) => [
          key, 
          ((value / testResults.length) * 100).toFixed(2)
        ])
      )
    },
    devicePerformance: Object.values(devicePerformance)
      .sort((a, b) => b.averagePerformance - a.averagePerformance),
    manufacturerPerformance: Object.entries(manufacturerPerformance)
      .map(([name, data]) => ({ name, ...data }))
      .sort((a, b) => b.averagePerformance - a.averagePerformance)
  };
}

export function identifyCommonIssues(testResults) {
  const issueFrequency = {};

  testResults.forEach(result => {
    result.issues?.forEach(issue => {
      issueFrequency[issue] = (issueFrequency[issue] || 0) + 1;
    });
  });

  return Object.entries(issueFrequency)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5)
    .map(([issue, count]) => ({ issue, count }));
}

export function exportTestResults(testResults) {
  const csvContent = [
    ['URL', 'Device', 'Manufacturer', 'Screen Size', 'Compatibility', 'Performance Score', 'Responsive Score', 'Issues', 'Timestamp']
  ];

  testResults.forEach(result => {
    csvContent.push([
      result.url,
      result.device.name,
      result.device.manufacturer,
      `${result.screenSize.width}x${result.screenSize.height}`,
      result.compatibility,
      result.performanceScore.toFixed(2),
      result.responsiveScore.toFixed(2),
      result.issues.join('; '),
      result.timestamp
    ]);
  });

  const csvString = csvContent.map(e => e.join(",")).join("\n");
  const blob = new Blob([csvString], { type: 'text/csv;charset=utf-8;' });
  const link = document.createElement("a");
  const url = URL.createObjectURL(blob);
  link.setAttribute("href", url);
  link.setAttribute("download", `mobile_testing_results_${new Date().toISOString()}.csv`);
  link.style.visibility = 'hidden';
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}
